<template>
  <h1 >加法</h1>
  <div class="yiyijia">
   <span>最小数<p>(可选)</p></span><input type="text" v-model="mins"> <span>最大数<p>(可选)</p></span><input type="text" v-model="maxs">
   <button @click="update()">更新</button>
  </div>
  <div v-for="(v,i) of nums" :key="i" >
    <span>{{ v.pre }}</span><p>+</p><span>{{v.next}}</span><p>=</p>
    <input type="text"  class="result" ref="results" @keyup="inputRef($event,i)">
