      maxs:16,
      minss:1,
      maxss:16,
      overjia:0
    })

    //随机数函数
    h.random=(min, max)=> {
      return Math.floor(Math.random() * (max - min)) + min;
    }
    //随机生成20组对象并增加为一个数组
    for (let i=0; i<50;i++){
      const obj={pre:h.random(h.minss,h.maxss),next:h.random(h.minss,h.maxss)}
      h.nums.push(obj)
    }

